<template>
    <div>
      <img :src="'data:image/png;base64,' + base64Image" alt="Word Cloud" />
    </div>
  </template>
  
  <script>
 import WordcloudService from '@/services/WordcloudService';

  
  export default {
    name:'wordcloudComponent',
    data() {
      return {
        base64Image: '',
        code: ""

      };
    },
    async created() {
    try {
      const response = await WordcloudService.generateWordcloud();
      
      this.base64Image = JSON.stringify((response.data)).split(',');
    } catch (error) {
      console.error(error);
    }
  },
  };
  </script>
  